<span class="line"><span style="color:#F8F8F2;">column_sums </span><span style="color:#F92672;">=</span><span style="color:#F8F8F2;"> [</span><span style="color:#AE81FF;">0</span><span style="color:#F8F8F2;">] </span><span style="color:#F92672;">*</span><span style="color:#66D9EF;"> len</span><span style="color:#F8F8F2;">(matrix[</span><span style="color:#AE81FF;">0</span><span style="color:#F8F8F2;">])</span></span>
